import React, { Component } from 'react'
import NavBranch from '../NavBranch/NavBranch'
import {menu} from '../../../data/menu';
import style from './NavMain.module.scss';

export default class NavMain extends Component {
    constructor (props) {
        super(props);
        this.state = {
            colapsed: false
        }
    }
    collapse = () => {
        debugger;
        this.setState({
            colapsed: !this.state.colapsed
        })
    }
    renderNav = () => {
        const {colapsed} = this.state;
        let result = menu.map((item, index) =>{
           let subItem = [
               <p className = "group-nav-name" onClick = {() => this.collapse()}>{item.title}</p>,
               <div className={`group-nav-area ${colapsed ? "colapsed": ""}`}
               >
                    {
                        item.sub.map((item2, index2) => {
                            return <NavBranch />
                        })
                    }
               </div>

           ]
           return subItem.flat();
        });
        
        // out result
        return result.map((item, index) => {
            return (
                <div>
                    {
                        item
                    }
                </div>
            )
        });

    }
    render() {
        return (
            <div className = {style.NavMain}>
                {this.renderNav()}
                {/* <NavBranch /> */}
            </div>
        )
    }
}
